mapper.insert:
INSERT INTO TB_QNA(
                    QNO
                  ,QUESTIONER
                  ,QUESTION
                  ,ANSWER
                  ,ANSWERER
                  ,INSERT_TIME
)
VALUES(
        SQ_FAQ.NEXTVAL
      , #{questioner}
      , #{question}
      , #{answer}
      , #{answerer}
      ,SYSDATE)

mapper.update:
UPDATE TB_QNA
SET QUESTIONER = #{questioner}
  ,QUESTION   = #{question}
  ,ANSWER = #{answer}
  ,ANSWERER   = #{answerer}
  ,UPDATE_TIME = SYSDATE
WHERE QNO = #{qno}
